mapper.select:
select 
	a.*,
	b.lookup_name as department_name
from employee a
	inner join lookup b on a.fk_department = b.pk_lookup
where 1 = 1

		and a.pk_employee = #{pkEmployee}


		and a.is_active = #{isActive}


		and a.employee_name = #{employeeName}

order by a.employee_name asc

    	select count(*) from employee a
    	where 1 = 1

				and a.is_active = #{isActive}
