mapper.select:
select a.* from pelajaran a where 1 = 1
and a.pk_pelajaran = #{pkPelajaran}
and lower(a.nama_pelajaran) like #{namaPelajaran}
order by a.pk_pelajaran asc

mapper.insert:
		insert into pelajaran ( 
			nama_pelajaran,
			created_date, 
			created_by
		) values (
			#{namaPelajaran},
			#{createdDate},
			#{createdBy}
      )

mapper.update:
		update pelajaran
    	set 
    		nama_pelajaran = #{namaPelajaran},
	      	changed_date = #{changedDate},
	      	changed_by = #{changedBy}
		where pk_pelajaran = #{pkPelajaran}
