mapper.select:
select a.pk_pendaftaran, b.pk_siswa, b.nama_siswa, c.nilai, c.fk_pelajaran, d.nama_pelajaran from pendaftaran a join siswa b on b.pk_siswa = a.fk_siswa join pendaftaran_detail c on a.pk_pendaftaran = c.fk_pendaftaran join pelajaran d on d.pk_pelajaran = c.fk_pelajaran where 1 = 1
and a.pk_pendaftaran = #{pkPendaftaran}
order by a.pk_pendaftaran asc

mapper.insert:
		insert into pendaftaran ( 
			fk_siswa,
			created_date, 
			created_by
		) values (
			#{fkSiswa},
			#{createdDate},
			#{createdBy}
      )

mapper.update:
		update pendaftaran
    	set 
	      	changed_date = #{changedDate},
	      	changed_by = #{changedBy}
		where pk_pendaftaran = #{pkPendaftaran}
