mapper.select:
select 
	a.*,
	b.status_name
from po a
	inner join app_status b on b.status_code = a.status and b.status_type = 'PO'
	inner join employee c on c.pk_employee = a.fk_employee
	inner join lookup d on d.pk_lookup = c.fk_department
where 1 = 1

		and a.pk_po = #{pkPo}


		and a.status = #{status}


		and a.fk_employee = #{fkEmployee}


		and d.pk_lookup = #{fkDepartment}


		and lower(a.po_no) like #{poNo}


		and a.status in 

       		#{statusList}


order by a.po_date desc

    	select count(*) from po a
    	where 1 = 1

				and a.status = #{status}


				and lower(a.po_no) like #{poNo}


				and a.fk_employee = #{fkEmployee}


				and d.pk_lookup = #{fkDepartment}

mapper.update:
	update po


      		fk_employee = #{fkEmployee},


			status = #{status},

      	changed_date = #{changedDate},
      	changed_by = #{changedBy}

	where pk_po = #{pkPo}
