mapper.select:
select 
	a.*,
	b.po_no
from delivery a
	inner join po b on b.pk_po = a.fk_po
where 1 = 1

		a.fk_po = #{fkPo}


		and lower(a.delivery_no) like #{deliveryNo}


		and lower(b.po_no) like #{poNo}

order by a.delivery_date desc

    	select count(*) from delivery a
    	where 1 = 1

				and lower(a.delivery_no) like #{deliveryNo}


				and lower(b.po_no) like #{poNo}

mapper.update:
update delivery
delivery_date = #{deliveryDate}, changed_date = #{changedDate}, changed_by = #{changedBy},
where pk_delivery = #{pkDelivery}
